mapper.select:
SELECT * FROM inquiry
ORDER BY id DESC
LIMIT #{index}, 10;

SELECT * FROM inquiry
WHERE userid = #{userid}
ORDER BY writeday DESC
LIMIT #{index}, 10;

mapper.update:
	UPDATE inquiry 
	SET title = #{title}, content = #{content}, category = #{category}, phone = #{phone}, status = #{status}
	WHERE id = #{id}
	
    	AND pwd = #{pwd}
	;
